INSERT OR REPLACE INTO LocalizedText 
(Tag, Text, Language) VALUES

('BBS_SRESOURCES_GARANTEED_NAME', 'Spawn Guaranteed', 'en_US'),

('LOC_GOLD_LABEL', ' [COLOR_FLOAT_GOLD][GOLD][ENDCOLOR]', 'en_US'),
('LOC_DB_GOLD_MOD_NOTE', '[NEWLINE][NEWLINE][COLOR_Red]from mod "DB''s [ICON_Gold]Civ VI Gold[ICON_Gold]"[ENDCOLOR]', 'en_US'),
('LOC_DB_OPTIONS_NOTE', 'Options from the mod "DB''s [ICON_Gold]Civ VI Gold[ICON_Gold]"', 'en_US'),
('LOC_DB_XP1_NOTE', '[NEWLINE][COLOR_Red]Note: does not work unless you are using Rise and Fall[ENDCOLOR]', 'en_US'),
('LOC_DB_XP2_NOTE', '[NEWLINE][COLOR_Red]Note: does not work unless you are using Gathering Storm[ENDCOLOR]', 'en_US'),
('LOC_DB_BABYLON_NOTE', '[NEWLINE][COLOR_Red]Note: does not work unless you are using the Babylon DLC[ENDCOLOR]', 'en_US'),
('LOC_DB_ETHIOPIA_NOTE', '[NEWLINE][COLOR_Red]Note: does not work unless you are using the Ethiopia DLC[ENDCOLOR]', 'en_US'),
('LOC_DB_GRAN_COLOMBIA_NOTE', '[NEWLINE][COLOR_Red]Note: does not work unless you are using the Gran Colombia DLC[ENDCOLOR]', 'en_US'),

('LOC_DB_GOLD_NAME', '[ICON_Gold][ICON_Gold]Civ VI Gold[ICON_Gold][ICON_Gold]', 'en_US'),

('LOC_DB_ECONOMIC_OPTIONS_DESC', 'Economic {LOC_DB_OPTIONS_NOTE}', 'en_US'),
('LOC_DB_LOYALTY_OPTIONS_DESC', 'Loyalty {LOC_DB_OPTIONS_NOTE}', 'en_US'),
('LOC_DB_MILITARY_OPTIONS_DESC', 'Military {LOC_DB_OPTIONS_NOTE}', 'en_US'),
('LOC_DB_RELIGION_OPTIONS_DESC', 'Religion {LOC_DB_OPTIONS_NOTE}', 'en_US'),
('LOC_DB_COSMETIC_OPTIONS_DESC', 'Cosmetic {LOC_DB_OPTIONS_NOTE}', 'en_US'),
('LOC_DB_OK_SINGLEPLAYER_OPTIONS_DESC', '{LOC_DB_OPTIONS_NOTE} that definitely should be enabled for a multiplayer game and will work fine in single player if you want to use them', 'en_US'),
('LOC_DB_ONLY_MULTIPLAYER_OPTIONS_DESC', '{LOC_DB_OPTIONS_NOTE} that definitely should be enabled for a multiplayer game, but are disabled by default because the AI can''t handle them properly', 'en_US'),
('LOC_DB_DEPRECATED_OPTIONS_DESC', '{LOC_DB_OPTIONS_NOTE} that I made but no longer play with', 'en_US'),

('LOC_DB_ALL_CITIES_LIKE_SUGAR_DESC', 'Luxury resources provide amenities to all your cities, not just 4{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_ALL_CITIES_LIKE_SUGAR_NAME', 'All Cities Like Sugar', 'en_US'),

('LOC_DB_ALL_CUSTOM_NAMES_DESC', 'The names for all religions can be customized, but the AI chooses blank names{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_ALL_CUSTOM_NAMES_NAME', 'All Religions Can Have Custom Names', 'en_US'),

('LOC_DB_ALL_HS3_BUILDINGS_DESC', 'All level 3 Holy Site buildings (Cathedral, Mosque, Pagoda, etc.) are available when the Divine Right civic is obtained. The Worship ("building") class of beliefs is removed.{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_ALL_HS3_BUILDINGS_NAME', 'All Holy Site Buildings', 'en_US'),

('LOC_DB_ALL_MEN_ARE_MORTAL_DESC', 'When Great People die, they die. (Same applies to archeologists and naturalists.){LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_ALL_MEN_ARE_MORTAL_NAME', 'All Men Are Mortal', 'en_US'),

('LOC_DB_BALANCED_WALLS_DESC', '[ICON_Bullet]Ancient Walls have no ranged attack.[NEWLINE][ICON_Bullet]Medieval Walls get a single ranged attack with a range of 1.[NEWLINE][ICON_Bullet]Renaissance Walls get two ranged attacks with range of 2.[NEWLINE][ICON_Bullet]Victor''s Embrasure promotion provides an extra ranged attack AND an extra range.{LOC_DB_GOLD_MOD_NOTE}{LOC_DB_XP2_NOTE}', 'en_US'),
('LOC_DB_BALANCED_WALLS_NAME', 'Balanced Walls', 'en_US'),

('LOC_DB_BATTLE_NOTIFICATIONS_DESC', '[ICON_BULLET]Each battle creates a notification with all the combat values involved[NEWLINE][ICON_BULLET]When your units die on defense, you get vision of the area for a turn[NEWLINE][ICON_BULLET]All players receive notifications when WMDs are launched{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_BATTLE_NOTIFICATIONS_NAME', 'Battle Notifications', 'en_US'),

('LOC_DB_BETTER_DIPLOMATIC_QUARTER_DESC', '[ICON_BULLET]Consulate: influence point generation and all yields doubled[NEWLINE][ICON_Bullet]Chancery: influence point generation and all yields doubled, unlocked at Mathematics (instead of Diplomatic Service)[NEWLINE][ICON_Bullet]Recruit envoy (new project): add 1 envoy[NEWLINE][ICON_Bullet]Expand espionage (new project): add 1 spy slot{LOC_DB_GOLD_MOD_NOTE}{LOC_DB_ETHIOPIA_NOTE}', 'en_US'),
('LOC_DB_BETTER_DIPLOMATIC_QUARTER_NAME', 'Better Diplomatic Quarter', 'en_US'),

('LOC_DB_BETTER_GOVERNMENT_PLAZA_DESC', '[ICON_Bullet]Warlord''s Throne: 10 turns of production (instead of 5)[NEWLINE][ICON_Bullet]Foreign Ministry: Levying units half-off; levied units get +7[ICON_Strength] (instead of +4[ICON_Strength])[NEWLINE][ICON_Bullet]Grandmaster''s Chapel: Spreads religious pressure to nearby cities upon killing an enemy unit; No longer allows faith buys{LOC_DB_GOLD_MOD_NOTE}{LOC_DB_XP1_NOTE}', 'en_US'),
('LOC_DB_BETTER_GOVERNMENT_PLAZA_NAME', 'Better Government Plaza', 'en_US'),

('LOC_DB_BETTER_GOVERNORS_DESC', 'Each governor was overhauled to create interesting decisions and governor "promotions" were stripped of their hierarchy (i.e, you can get any promotion at any time). Most Governors install in 3 turns instead of 5.{LOC_DB_GOLD_MOD_NOTE}{LOC_DB_GRAN_COLOMBIA_NOTE}', 'en_US'),
('LOC_DB_BETTER_GOVERNORS_NAME', 'Better Governors', 'en_US'),

('LOC_DB_BETTER_GREAT_SCIENTISTS_DESC', 'Great Scientists who trigger Eurekas to several Techs are replaced by Great Scientists who research one specific Tech in full, even if that Tech''s prerequisites haven''t been researched. Combat units on these granted technologies have been moved to other techs so that the Great Scientists don''t allow for too much of a military advantage. Finally, a handful of Great Scientists have been moved to earlier eras.{LOC_DB_GOLD_MOD_NOTE}{LOC_DB_XP2_NOTE}{LOC_DB_BABYLON_NOTE}', 'en_US'),
('LOC_DB_BETTER_GREAT_SCIENTISTS_NAME', 'Better Great Scientists', 'en_US'),

('LOC_DB_BETTER_ROADS_DESC', 'Ancient Roads support bridges and all roads are slightly faster{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_BETTER_ROADS_NAME', 'Better Roads', 'en_US'),

('LOC_DB_BETTER_SCOUTS_DESC', 'Scouts are now support units with a new promotion menu that assists nearby military units with movement, vision, and combat{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_BETTER_SCOUTS_NAME', 'Better Scouts', 'en_US'),

('LOC_DB_BETTER_TRADE_ROUTES_DESC', 'Makes international (including city-state) trade routes better than domestic routes. Otherwise, why bother with international routes?[NEWLINE][ICON_Bullet]International yields match domestic yields in addition to their standard yield. (e.g., domestic campus +[ICON_FOOD]; foreign campus +[ICON_FOOD][ICON_SCIENCE])[NEWLINE][ICON_Bullet]City States only request trade routes as their quest[NEWLINE][ICON_Bullet]Trade routes only go to their destination once (no matter the distance){LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_BETTER_TRADE_ROUTES_NAME', 'Better Trade Routes', 'en_US'),

('LOC_DB_BETTER_WARFARE_DESC', 'Unit combat values, costs, and resource requirements are balanced. Military promotion trees are updated.{LOC_DB_GOLD_MOD_NOTE}{LOC_DB_XP2_NOTE}', 'en_US'),
('LOC_DB_BETTER_WARFARE_NAME', 'Better Warfare', 'en_US'),

('LOC_DB_BUILDERS_CAN_BUILD_ROADS_DESC', 'Builders and military engineers can spend a build charge to build a road{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_BUILDERS_CAN_BUILD_ROADS_NAME', 'Builders Can Build Roads', 'en_US'),

('LOC_DB_BUY_HSBUILDINGS_WITH_FAITH_DESC', 'All Holy Site buildings can be purchased with gold or faith by default{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_BUY_HSBUILDINGS_WITH_FAITH_NAME', 'Buy Holy Site Buildings With Faith', 'en_US'),

('LOC_DB_CATHEDRALS_HOLD_RELICS_DESC', 'Cathedrals can hold religious art, a relic, or a piece of music. Normally they can only hold religious art.{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_CATHEDRALS_HOLD_RELICS_NAME', 'Cathedrals Hold Relics', 'en_US'),

('LOC_DB_CITYSTATES_ARENT_BACKWARDS_DESC', 'City-states inevitably fall behind the major civilizations because they lack so many advantages that players have at their disposal: having multiple cities, assigning governors, pantheon bonuses, acquiring great people, getting boosts from tribal villages, earning bonuses from sending envoys to city-states, etc. This option immediately gives city-states any new technology or civic that a neighboring civilization has discovered and some other built-in bonuses to help them thrive. For players, this makes envoys (and consequently, culture) more important.[NEWLINE][ICON_BULLET]1 [ICON_Food] per-population[NEWLINE][ICON_BULLET]2 [ICON_Production] per-population[NEWLINE][ICON_BULLET]5 [ICON_Gold] per-population[NEWLINE][ICON_BULLET]City States are also no longer restricted to building only their type of district{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_CITYSTATES_ARENT_BACKWARDS_NAME', 'City-States Aren''t Backwards', 'en_US'),

('LOC_DB_COMBAT_DOESNT_PRESSURE_DESC', 'Theological Combat no longer affects the religious pressure in nearby cities{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_COMBAT_DOESNT_PRESSURE_NAME', 'Combat Doesn''t Pressure', 'en_US'),

('LOC_DB_COURTHOUSES_DESC', 'Adds the Courthouse building[NEWLINE][ICON_Bullet]Each courthouse grants the player a governor title[NEWLINE][ICON_Bullet]Can be built in any City Center that has a Monument[NEWLINE][ICON_Bullet]Unlocked with State Workforce{LOC_DB_GOLD_MOD_NOTE}{LOC_DB_XP1_NOTE}', 'en_US'),
('LOC_DB_COURTHOUSES_NAME', 'Courthouses', 'en_US'),

('LOC_DB_CULTURE_PER_POP_0_DESC', 'Each population in each city creates 0 [ICON_Culture]{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_CULTURE_PER_POP_0_NAME', '0', 'en_US'),
('LOC_DB_CULTURE_PER_POP_10_DESC', 'Each population in each city creates 0.10 [ICON_Culture]{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_CULTURE_PER_POP_10_NAME', '0.10', 'en_US'),
('LOC_DB_CULTURE_PER_POP_15_DESC', 'Each population in each city creates 0.15 [ICON_Culture]{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_CULTURE_PER_POP_15_NAME', '0.15', 'en_US'),
('LOC_DB_CULTURE_PER_POP_20_DESC', 'Each population in each city creates 0.20 [ICON_Culture]{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_CULTURE_PER_POP_20_NAME', '0.20', 'en_US'),
('LOC_DB_CULTURE_PER_POP_25_DESC', 'Each population in each city creates 0.25 [ICON_Culture] (recommended){LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_CULTURE_PER_POP_25_NAME', '0.25 (recommended)', 'en_US'),
('LOC_DB_CULTURE_PER_POP_30_DESC', 'Each population in each city creates 0.30 [ICON_Culture] (normal, unmodded value){LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_CULTURE_PER_POP_30_NAME', '0.30 (default, unmodded value)', 'en_US'),
('LOC_DB_CULTURE_PER_POP_5_DESC', 'Each population in each city creates 0.05 [ICON_Culture]{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_CULTURE_PER_POP_5_NAME', '0.05', 'en_US'),
('LOC_DB_CULTURE_PER_POP_DISABLED_NAME', 'Disabled', 'en_US'),

('LOC_DB_CULTURE_PER_POP_DESC', 'The amount of culture each population in your cities inherently generates (i.e. generated without help from districts, buildings, worked tiles, governors, policies, etc.). Without this mod, it would be 0.30. A slowing effect is definitely needed when playing with the "Learn From Other Civs" option.{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_CULTURE_PER_POP_LABEL', 'Culture Per Population', 'en_US'),
('LOC_DB_CULTURE_PER_POP_NAME', 'Culture Per Population', 'en_US'),

('LOC_DB_DIFFICULTY_COMBAT_STRENGTH_DESC', 'Human players'' units get a built-in [ICON_Strength] Combat Strength bonus based on the difficulty setting they choose at the beginning of the game. Useful for handicapping multiplayer games that involve players of different abilities.[NEWLINE][NEWLINE]The [ICON_Strength] Combat Strength values are always listed on the difficulty selection boxes during game setup, but they will only be in effect in the game when this option is selected.{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_DIFFICULTY_COMBAT_STRENGTH_NAME', 'Difficulty = Combat Strength', 'en_US'),
('LOC_DIFFICULTY_DEITY_NAME',		'Deity (+0 [ICON_Strength])', 'en_US'),
('LOC_DIFFICULTY_IMMORTAL_NAME',	'Immortal (+2 [ICON_Strength])', 'en_US'),	
('LOC_DIFFICULTY_EMPEROR_NAME',		'Emperor (+4 [ICON_Strength])', 'en_US'),
('LOC_DIFFICULTY_KING_NAME',		'King (+6 [ICON_Strength])', 'en_US'),
('LOC_DIFFICULTY_PRINCE_NAME',		'Prince (+8 [ICON_Strength])', 'en_US'),
('LOC_DIFFICULTY_WARLORD_NAME',		'Warlord (+10 [ICON_Strength])', 'en_US'),
('LOC_DIFFICULTY_CHIEFTAIN_NAME',	'Chieftain (+12 [ICON_Strength])', 'en_US'),	
('LOC_DIFFICULTY_SETTLER_NAME',		'Settler (+14 [ICON_Strength])', 'en_US'),

('LOC_DB_DISLOYAL_INFORMANTS_DESC', 'Gain vision in foreign cities when you have some loyalty there.[NEWLINE][ICON_Bullet]1-10 Loyalty = vision of the city center[NEWLINE][ICON_Bullet]10-19 Loyalty = 1-tile radius of vision[NEWLINE][ICON_Bullet]20+ Loyalty = 2-tile radius of vision{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_DISLOYAL_INFORMANTS_NAME', 'Disloyal Informants', 'en_US'),

('LOC_DB_DISTRICTS_ON_RESOURCES_DESC', 'Districts may be placed on Resource tiles, but placing the district (not finishing it) immediately removes the resource for no benefit[NEWLINE][ICON_Bullet]Note: With Olympic Pantheons Combat, this option has no effect on the resources associated with a god{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_DISTRICTS_ON_RESOURCES_NAME', 'Districts On Resources', 'en_US'),

('LOC_DB_DONT_NEED_INQUISITION_DESC', 'Removes inquisitions from the game. Inquisitors can simply be purchased with faith from a Temple.{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_DONT_NEED_INQUISITION_NAME', 'Inquisitors: Don''t Need An Inquisition', 'en_US'),

('LOC_DB_DOUBLE_BELIEFS_DESC', 'Players get 2 beliefs for each Great Prophet instead of just 1{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_DOUBLE_BELIEFS_NAME', 'Soli Deo Gloria: Double Beliefs', 'en_US'),

('LOC_DB_DOUBLE_SPIES_DESC', 'Techs and civics that grant a spy will now grant 2. (Recommended to go with Remove Foreign Info, as the extra spies will help make up for your lack of clairvoyance.){LOC_DB_GOLD_MOD_NOTE}[NEWLINE][Idea thanks to FearSunn]', 'en_US'),
('LOC_DB_DOUBLE_SPIES_NAME', 'Double Spy Capacity', 'en_US'),

('LOC_DB_EARLIER_WARS_OF_RELIGION_DESC', 'The Wars of Religion military policy is acquired at Theology and expires at National Identity (where corps are unlocked). The Holy War casus belli is also available at Theology.{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_EARLIER_WARS_OF_RELIGION_NAME', 'Earlier Wars of Religion', 'en_US'),

('LOC_DB_ENGINEERS_AS_SAPPERS_DESC', 'Once the Siege Tactics tech has been learned, Melee and Anti-Cavalry units adjacent to a Military Engineer do full damage to city walls{LOC_DB_GOLD_MOD_NOTE}{LOC_DB_XP2_NOTE}[NEWLINE][Idea thanks to JNR]', 'en_US'),
('LOC_DB_ENGINEERS_AS_SAPPERS_NAME', 'Engineers Are Also Sappers', 'en_US'),

('LOC_DB_ENGINEERS_RUSH_WALLS_DESC', '[ICON_Bullet]Military Engineers can use a build charge to add +50%/+20%/+15% production to Ancient/Medieval/Renaissance Walls[NEWLINE][ICON_Bullet]Roman Legions can use a build charge to add +50% production to Ancient Walls{LOC_DB_GOLD_MOD_NOTE}{LOC_DB_XP2_NOTE}[NEWLINE][Idea thanks to JNR]', 'en_US'),
('LOC_DB_ENGINEERS_RUSH_WALLS_NAME', 'Engineers Can Rush Walls', 'en_US'),

('LOC_DB_EMBARKED_RELIGIOUS_UNITS_DESC', 'Religious units can only enter water tiles when they are adjacent to a naval unit. If a religious unit is embarked and naval units are no longer adjacent to it, it will die at the end of the turn. (The AI is exempt from this because it can''t learn how to avoid drowning its units.){LOC_DB_GOLD_MOD_NOTE}{LOC_DB_XP2_NOTE}', 'en_US'),
('LOC_DB_EMBARKED_RELIGIOUS_UNITS_NAME', 'No Religious Sea-Spies', 'en_US'),

('LOC_DB_EXPERIENCE_FROM_OBSERVATION_DESC', 'Military units gain experience from observing adjacent melee attacks. +1 XP if next to a defending unit or an enemy unit in a melee battle. +2 XP if next to both.{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_EXPERIENCE_FROM_OBSERVATION_NAME', 'XP From Observation', 'en_US'),

('LOC_DB_GOLD_FROM_TOURISM_DESC', 'Foreign tourists provide your empire with gold per turn.{LOC_DB_GOLD_MOD_NOTE}[NEWLINE][Idea thanks to FearSunn]', 'en_US'),
('LOC_DB_GOLD_FROM_TOURISM_NAME', 'Gold From Tourism', 'en_US'),

('LOC_DB_GREAT_MERCHANTS_GRANT_TRADER_DESC', 'All great merchants grant a trade route in addition to their other abilities... otherwise great merchants tend to be weak compared to the other great people. Commercial hubs and their buildings are thus more desirable.{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_GREAT_MERCHANTS_GRANT_TRADER_NAME', 'Great Merchants are Merchants', 'en_US'),

('LOC_DB_GREAT_PEOPLE_COME_FROM_PEOPLE_DESC', 'This option changes how districts provide great people points. Great people now come from people. Each specialist creates a Great Person Point, and the (empty) districts and buildings do not.[NEWLINE][NEWLINE]Highly, highly recommended for multiplayer, but disabled by default because the AI can''t figure out how to make great people points when this option is enabled.[NEWLINE][NEWLINE]Affected districts include:[NEWLINE][ICON_DISTRICT_CAMPUS]Campus (Seowon, Observatory)[NEWLINE][ICON_DISTRICT_COMMERCIAL_HUB]Commercial Hub (Suguba)[NEWLINE][ICON_DISTRICT_ENCAMPMENT]Encampment (Ikanda)[NEWLINE][ICON_DISTRICT_HARBOR]Harbor (Cothon, Royal Navy Dockyard)[NEWLINE][ICON_DISTRICT_HOLYSITE]Holy Site (Lavra)[NEWLINE][ICON_DISTRICT_INDUSTRIAL_ZONE]Industrial Zone (Hansa, Oppidum)[NEWLINE][ICON_DISTRICT_THEATER]Theater Square (Acropolis)[NEWLINE][NEWLINE]Known bug:[NEWLINE][ICON_Bullet]The code is written such that each specialist should provide one great person point, but in testing I noticed that it instead gives two points per specialist. Firaxis, if you''re reading this, table "District_CitizenGreatPersonPoints" is not functioning accurately.{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_GREAT_PEOPLE_COME_FROM_PEOPLE_NAME', 'Great People Come From People', 'en_US'),

('LOC_DB_INQUISITORS_FROM_SHRINES_DESC', 'Instead of Temples{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_INQUISITORS_FROM_SHRINES_NAME', 'Inquisitors: Purchased from Shrines', 'en_US'),

('LOC_DB_KOTOKUIN_TO_THEOLGY_DESC', 'The Kotoku-In wonder moves from the Divine Right civic to the Theology civic and its production cost changes from 710 to 400{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_KOTOKUIN_TO_THEOLGY_NAME', 'Kotoku-In Moved To Theology', 'en_US'),

('LOC_DB_LEARN_FROM_OTHER_CIVS_DESC', 'It''s harder to invent the wheel than to reinvent it.[NEWLINE][NEWLINE]Whenever another civ that you''ve met has completed a tech/civic, you get a science/culture bonus while you are researching it, and a bigger bonus if you have Diplomatic Visibility on them (e.g. spies, embassies, etc).{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_LEARN_FROM_OTHER_CIVS_NAME', 'Learn From Other Civs', 'en_US'),

('LOC_DB_LIGHTHOUSES_REMOVE_TRADE_ROUTE_DESC', 'Lighthouses no longer provide a trade route so that they don''t super-dominate markets. Commercial hubs and markets are thus more desirable.{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_LIGHTHOUSES_REMOVE_TRADE_ROUTE_NAME', 'Lighthouses Remove Their Trade Route', 'en_US'),

('LOC_DB_LOYALTY_MATTERS_DESC', '[ICON_Bullet]Wonders, Great Works, Theater Squares, Entertainment Complexes, Water Parks and buildings for those districts each provide 1 loyalty to all your cities within 9 tiles and -1 loyalty to all foreign cities within 9 tiles[NEWLINE][ICON_Bullet]Monuments provide 2 loyalty to all your cities within 9 tiles and -2 loyalty to all foreign cities within 9 tiles[NEWLINE][ICON_Bullet]The Foment Unrest mission for spies reduces loyalty by 100 (instead of 20), but even so, the city will not revolt unless the city''s loyalty-per-turn is negative{LOC_DB_GOLD_MOD_NOTE}{LOC_DB_XP1_NOTE}', 'en_US'),
('LOC_DB_LOYALTY_MATTERS_NAME', 'Loyalty Matters', 'en_US'),

('LOC_DB_MARTYRS_LOSE_CS_DESC', 'Apostles with a Martyr promotion lose 100 combat strength (110 --> 10; spread strength does not change). They still create a relic upon death.{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_MARTYRS_LOSE_CS_NAME', 'Weak Martyrs', 'en_US'),

('LOC_DB_MILITARY_ALLIANCE_ONLY_SHARE_VISION_DESC', 'No Combat Bonus[NEWLINE][NEWLINE]When Military Alliances give a +5 [ICON_Strength] bonus, it amplifies the king-making dynamic already present in ffa games. I mean it''s bad enough that one guy is getting double-teamed by his brothers, why should all their units get +5 for doing so?[NEWLINE][NEWLINE]Second, it squashes the incentive to gift a player a city just to keep him alive for your +5 strength bonus.{LOC_DB_GOLD_MOD_NOTE}{LOC_DB_XP1_NOTE}', 'en_US'),
('LOC_DB_MILITARY_ALLIANCE_ONLY_SHARE_VISION_NAME', 'Militiary Alliance: Only Vision', 'en_US'),

('LOC_DB_MONUMENTS_NO_CULTURE_DESC', 'Monuments no longer give culture as a yield, but instead they provide the city with +2 loyalty and +100% border expansion. The Drama and Poetry civic (which unlocks Theater Squares) is moved to the Ancient Era. This makes Theater Squares more important because you can''t get easy culture from your monuments.{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_MONUMENTS_NO_CULTURE_NAME', 'Monuments: No Culture', 'en_US'),

('LOC_DB_MONUMENTS_PROVIDE_BUILD_CHARGE_DESC', 'Builders get an extra charge when built in a city with a monument{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_MONUMENTS_PROVIDE_BUILD_CHARGE_NAME', 'Monuments Provide A Build Charge', 'en_US'),

('LOC_DB_MORE_BELIEFS_DESC', 'Includes belief options that I retired from the main set{LOC_DB_GOLD_MOD_NOTE}{LOC_DB_XP2_NOTE}', 'en_US'),
('LOC_DB_MORE_BELIEFS_NAME', 'Soli Deo Gloria: More Belief Options', 'en_US'),

('LOC_DB_NO_BAD_HUTS_NAME', 'No Bad Huts', 'en_US'),
('LOC_DB_NO_BAD_HUTS_DESC', 'Tribal Village gifts include exactly these options:[NEWLINE][ICON_Bullet]Relic[NEWLINE][ICON_Bullet]Tech Boost[NEWLINE][ICON_Bullet]Civic Boost[NEWLINE][ICON_Bullet]Envoy[NEWLINE][ICON_Bullet]Trader[NEWLINE][ICON_Bullet]Builder[NEWLINE][ICON_Bullet]120 Gold[NEWLINE][ICON_Bullet]Melee Unit[NEWLINE][ICON_Bullet]Ranged Unit[NEWLINE][ICON_Bullet]City Population[NEWLINE][ICON_Bullet]Unit Promotion{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),

('LOC_DB_NO_MORE_STIRRINGS_DESC', 'Removes the text from the loading screen that goes like this:[NEWLINE][NEWLINE]"From the first stirrings of life beneath water... to the great beasts of the Stone Age... to man taking his first upright steps, you have come far.� Now begins your greatest quest: from this early cradle of civilization on towards the stars."[NEWLINE][NEWLINE]Ditto for the analogous pablum when you start in other ages.{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_NO_MORE_STIRRINGS_NAME', 'No More Stirrings', 'en_US'),

('LOC_DB_OLYMPIC_PANTHEONS_DESC', '[ICON_Bullet]The pantheon choices are all replaced with powerful geography-based beliefs with names from Greek mythology. The idea is to provide big yield bonuses on desolate (like deserts or tundra) or singular types of terrain (like cattle or sheep) that thus becomes your "holy ground". This creates a huge incentive to forcibly take over someone else''s city when they aren''t respecting your holy ground. Or sacred cows.{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_OLYMPIC_PANTHEONS_NAME', 'Olympic Pantheons', 'en_US'),

('LOC_DB_OLYMPIC_PANTHEONS_COMBAT_DESC', '[ICON_Bullet]Olympic Pantheons provide combat bonuses when your units are near their gods'' tiles (in addition to the yield bonuses already provided).[NEWLINE][NEWLINE][ICON_Bullet]Resources and Marshes can no longer be removed, either by a builder or by placing a district (otherwise you could just harvest away your enemy''s resource type)[NEWLINE](i.e. This overrides the "Districts on Resources" game option for the resources associated with a god)[NEWLINE][NEWLINE]This used to be a standard part of Olympic Pantheons until it was discovered that there''s a bug where the combat bonus will stick to a unit if the bonus is active when the game is saved. (e.g. When you have Helios and a unit is on desert tile when the game is saved.) In single-player or real-time multiplayer the glitch is barely noticeable, but for Hotseat or Play By Cloud (my preference) it basically gives all units the pantheon''s combat bonus all the time. So, when this glitch was discovered, the combat element of OP was separated from the main part and moved here to the Hop-Ons section.{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_OLYMPIC_PANTHEONS_COMBAT_NAME', 'Olympic Pantheons: Combat', 'en_US'),

('LOC_DB_ONE_CLEANSE_INQUISITORS_DESC', 'Inquisitors only get one cleansing charge each, instead of the usual three (and don''t get bonus charges from Mosques or the Exodus of the Evangelicals golden age commemoration){LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_ONE_CLEANSE_INQUISITORS_NAME', 'Inquisitors: Only 1 Cleanse', 'en_US'),

('LOC_DB_PANTHEON_OBELISK_DESC', 'Players have to build a Pantheon Obelisk to found their pantheon and accrue faith. This building is available in your capital from the beginning of the game. The building doesn''t do anything after your pantheon is chosen.{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_PANTHEON_OBELISK_NAME', 'Pantheon Obelisk', 'en_US'),

('LOC_DB_PEOPLE_CREATE_DESC', 'This option changes how districts provide yields. It''s the people that create the yields, moreso than the buildings. A mine doesn''t really benefit you unless it''s being worked, so neither should districts. However, even though it now takes population to make your faith/science/culture/gold/production, you can get higher yields than before.[NEWLINE][NEWLINE]Details:[NEWLINE][NEWLINE]1) The districts themselves now include a specialist slot[NEWLINE]2) The inherent yields from the district buildings were reduced by 1 each[NEWLINE]3) Each building increases the yields for their specialists by 1[NEWLINE][NEWLINE]Consider the campus:[NEWLINE][ICON_Bullet]Campus specialists start out making [ICON_Science][NEWLINE][ICON_Bullet]For each campus building, all specialists make an additional [ICON_Science], thus when you have a library and university, each specialist will make [ICON_Science][ICON_Science][ICON_Science][NEWLINE][ICON_Bullet]The campus district itself and each building includes a specialist slot, so if you have a library and university, you can have 3 citizens working the campus tile[NEWLINE][ICON_Bullet]Each building inherently makes one fewer [ICON_Science], so now the library itself only generates [ICON_Science] and the university [ICON_Science][ICON_Science][ICON_Science][NEWLINE][NEWLINE]Affected districts include:[NEWLINE][ICON_DISTRICT_CAMPUS]Campus (Seowon, Observatory)[NEWLINE][ICON_DISTRICT_COMMERCIAL_HUB]Commercial Hub (Suguba)[NEWLINE][ICON_DISTRICT_ENCAMPMENT]Encampment (Ikanda)[NEWLINE][ICON_DISTRICT_HARBOR]Harbor (Cothon, Royal Navy Dockyard)[NEWLINE][ICON_DISTRICT_HOLYSITE]Holy Site (Lavra)[NEWLINE][ICON_DISTRICT_INDUSTRIAL_ZONE]Industrial Zone (Hansa, Oppidum)[NEWLINE][ICON_DISTRICT_THEATER]Theater Square (Acropolis){LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_PEOPLE_CREATE_NAME', 'People Create, Not Empty Buildings', 'en_US'),

('LOC_DB_RELIGIOUS_UNITS_TWO_MOVES_DESC', 'Missionaries, Apostles, Inquisitors and Gurus only have 2 base movement. Normally they have 4 (and can enter foreign territory), which makes them better scouts than Scouts.{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_RELIGIOUS_UNITS_TWO_MOVES_NAME', 'Religious Units: Only 2 Movement', 'en_US'),

('LOC_DB_REMOVE_CIV_LEADER_TRAITS_DESC', 'Removes all leader and civ traits (bonuses, unique units, start location biases, etc.). No special snowflakes.[NEWLINE][NEWLINE]With single player, it can be fun to have unique traits to guide you as you play through your story. But I feel that in multiplayer the leaders aren''t balanced and so rather than playing a game where it''s one player''s skill against another, it''s mostly about who chose (or was dealt) the strongest leader.[NEWLINE][NEWLINE]Consider playing a chess game where one player starts with an extra knight at the beginning of the game, but 30-moves later the other player gets an extra queen. That could be an interesting game, but it''s less a contest about the players'' own chess skills and more about which "bonus unit" is overall better. This option makes it more like a game where all sides start with the same pieces.{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_REMOVE_CIV_LEADER_TRAITS_NAME', 'Remove Civ/Leader Traits', 'en_US'),

('LOC_DB_REMOVE_CIVIC_BOOSTS_DESC', 'Removes the organic inspirations (i.e. civic boosts) from the game. Inspirations are now acquired only by tribal villages, great people, or wonders. Slows down the rate at going through the civic tree, and your civilization doesn''t have to frantically do dozens of random "side-quests" to keep up with other civs. A slowing effect is definitely needed when playing with the "Learn From Other Civs" option.{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_REMOVE_CIVIC_BOOSTS_NAME', 'Remove Inspirations', 'en_US'),

('LOC_DB_REMOVE_CONDEMN_HERETIC_DESC', 'Removes the Condemn Heretic action from military units for human players. (The AI will still condemn heretics.){LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_REMOVE_CONDEMN_HERETIC_NAME', 'Remove Condemn Heretic', 'en_US'),

('LOC_DB_REMOVE_EMERGENCIES_DESC', 'Everything is fine.{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_REMOVE_EMERGENCIES_NAME', 'Remove Emergencies', 'en_US'),

('LOC_DB_REMOVE_FOREIGN_INFO_DESC', 'No clairvoyance. You don''t get to automatically know everything about every other civilization.[NEWLINE][ICON_BULLET]Removes world rankings screen[NEWLINE][ICON_BULLET]Removes stats from the leader ribbons[NEWLINE][ICON_BULLET]Removes info about other cities when they''re in the fog of war (e.g. their defensive strength, city health, wall health, population size)[NEWLINE][ICON_BULLET]Removes info about foreign religions on the religion screen[NEWLINE][ICON_BULLET]Removes the leader icons on the bottom of the Tech and Civic trees[NEWLINE][ICON_BULLET]Removes info about the foreign government''s type the Diplomacy screen[NEWLINE][ICON_BULLET]Removes info about relationships with other leaders on the Diplomacy screen[NEWLINE][ICON_BULLET]Removes city information from the dealmaker screen[NEWLINE][ICON_BULLET]Removes gold/resource quantity info from the dealmaker screen[NEWLINE][ICON_BULLET]Removes info about foreign progress toward great people[NEWLINE][ICON_BULLET]Great person recruitment notifications (and history) are anonymous[NEWLINE][ICON_BULLET]Wonder completion notifications are anonymous{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_REMOVE_FOREIGN_INFO_NAME', 'Remove Foreign Info', 'en_US'),

('LOC_DB_NO_CITY_TRADES_DESC', 'Cities are not listed on the make deal menu. (Further hides information about other civilizations.){LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_NO_CITY_TRADES_NAME', 'No City Trades', 'en_US'),

('LOC_DB_REMOVE_GOLDEN_DARK_AGES_DESC', 'I''ll define when I''m in a Golden Age.{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_REMOVE_GOLDEN_DARK_AGES_NAME', 'Remove Golden/Dark Ages', 'en_US'),

('LOC_DB_REMOVE_GOVERNMENT_PLAZAS_DESC', 'Never been a huge fan of the district, but especially don''t like how the Grandmaster''s Chapel affects the game. Faith is for making religious units, and that''s that.{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_REMOVE_GOVERNMENT_PLAZAS_NAME', 'Remove Government Plazas', 'en_US'),

('LOC_DB_REMOVE_HEATHEN_CONVERSION_DESC', 'Removes the Heathen Conversion promotion option from Apostles{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_REMOVE_HEATHEN_CONVERSION_NAME', 'Remove Heathen Conversion', 'en_US'),

('LOC_DB_REMOVE_ONE_TURN_POLICIES_DESC', 'Removes:[NEWLINE][ICON_Bullet]Professional Army[NEWLINE][ICON_Bullet]Retinues[NEWLINE][ICON_Bullet]Force Modernization[NEWLINE][ICON_Bullet]Land Surveyors[NEWLINE][ICON_Bullet]Expropriation[NEWLINE][NEWLINE]I don''t like the play where you save up all your cash, and then game the civics tree so that you upgrade all your soldiers (or buy a bunch of tiles) for discount like so:[NEWLINE][NEWLINE]1) Learn all-but-1-turn on civic A, then switch to civic B[NEWLINE]2) When you finish civic B you select civic A and the Professional Army policy[NEWLINE]3) Upgrade all your army for half-price[NEWLINE]4) Next turn you finish civic A and switch off Professional Army{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_REMOVE_ONE_TURN_POLICIES_NAME', 'Remove 1-Turn Policies', 'en_US'),

('LOC_DB_REMOVE_PLUNDER_POLICIES_DESC', 'Remove the plundering and pillaging government policies, because they allow massive yields for exploiting a weaker player.{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_REMOVE_PLUNDER_POLICIES_NAME', 'Remove Plunder Policies', 'en_US'),

('LOC_DB_REMOVE_TECH_BOOSTS_DESC', 'Removes the organic eurekas (i.e. tech boosts) from the game. Eurekas are now acquired only by tribal villages, spies, great people, or wonders. Slows down the rate at going through the tech tree, and your civilization doesn''t have to frantically do dozens of random "side-quests" to keep up with other civs. A slowing effect is definitely needed when playing with the "Learn From Other Civs" option.{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_REMOVE_TECH_BOOSTS_NAME', 'Remove Eurekas', 'en_US'),

('LOC_DB_REMOVE_UNIT_UPGRADES_DESC', '[ICON_Bullet]Player units can no longer upgrade, so that ancient era units with promotions won''t snowball into unstoppable world-dominating monsters with no counter.[NEWLINE][ICON_Bullet]Buildings, Great Generals, policies, and other effects that provide a percent experience bonus have that bonus increased. (e.g., a Barracks gives units +75% experience instead of +25%)[NEWLINE][ICON_Bullet]Promotions no longer heal[NEWLINE][ICON_Bullet]Warrior Monk promotions that increase combat strength only do so on attack[NEWLINE][ICON_Bullet]City States can still upgrade their units (otherwise they''re content with a pile of warriors/slingers until those units die){LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_REMOVE_UNIT_UPGRADES_NAME', 'Remove Unit Upgrades', 'en_US'),

('LOC_DB_REMOVE_WORLD_CONGRESS_DESC', 'Good riddance{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_REMOVE_WORLD_CONGRESS_NAME', 'Remove World Congress', 'en_US'),

('LOC_DB_REVEAL_STRATEGIC_RESOURCES_DESC', 'Strategic resources are visible from the beginning of the game, but the yield bonuses were taken away (e.g. the +Science on Iron). You can build cities and improvements on these resources in the Ancient era, however, you won''t gather, say, Uranium, until you have Combined Arms.[NEWLINE][NEWLINE]Also, the "Hephaestus" belief from Olympic Pantheons and the "I am Death" belief from Soli Deo Gloria are removed.{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_REVEAL_STRATEGIC_RESOURCES_NAME', 'Reveal Strategic Resources', 'en_US'),

('LOC_DB_SCIENCE_PER_POP_0_DESC', 'Each population in each city creates 0 [ICON_Science]{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_SCIENCE_PER_POP_0_NAME', '0', 'en_US'),
('LOC_DB_SCIENCE_PER_POP_10_DESC', 'Each population in each city creates 0.10 [ICON_Science]{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_SCIENCE_PER_POP_10_NAME', '0.10', 'en_US'),
