('LOC_DB_SCIENCE_PER_POP_5_NAME', '0.05', 'en_US'),
('LOC_DB_SCIENCE_PER_POP_50_DESC', 'Each population in each city creates 0.50 [ICON_Science] (normal, unmodded value){LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_SCIENCE_PER_POP_50_NAME', '0.50 (default, unmodded value)', 'en_US'),
('LOC_DB_SCIENCE_PER_POP_55_DESC', 'Each population in each city creates 0.55 [ICON_Science]{LOC_DB_GOLD_MOD_NOTE}', 'en_US'),
('LOC_DB_SCIENCE_PER_POP_55_NAME', '0.55', 'en_US'),
